import "./index.less";
import { useState, useEffect } from "react";
import { Swiper } from "antd-mobile";
import { useHistory } from "react-router-dom";
import {
  bannerItems,
  recommendedGames,
  otherGames,
} from "../../json/game/ index";
const Main = () => {
  const [banner, setBanner] = useState([]);
  const history = useHistory();
  const init = () => {
    const bannerArr = bannerItems.map((item, index) => {
      return (
        <Swiper.Item key={item.id} onClick={() => goDetailPage(item)}>
          <div>
            <img
              className="swiperImage"
              src={require(`~static/banner/${item.id}.jpg`)}
            ></img>
          </div>
        </Swiper.Item>
      );
    });
    setBanner(bannerArr);
  };

  useEffect(() => {
    init();
  }, []);

  const goDetailPage = (item) => {
    console.log(item);
    history.push({
      path: "/detail",
    });
  };

  return (
    <div className="gameListBox">
      {/* 轮播图 */}
      <Swiper
        style={{ height: 250 }}
        loop
        autoplay
        allowTouchMove
        autoplayInterval="3000"
        indicator={(total, current) => (
          <div className="customIndicator">
            {bannerItems.map((item, index) => (
              <div
                className={
                  current === index
                    ? "customIndicatorItem currentItem"
                    : "customIndicatorItem"
                }
              >
                <img src={require(`~static/banner/${item.id}-small.jpg`)}></img>
              </div>
            ))}
          </div>
        )}
      >
        {banner}
      </Swiper>
      <div className="floor-game">
        <div className="block-gradient"></div>
        <div className="block-recomended">
          <div className="title">Recommended</div>
          <div className="gamePart">
            <div className="top">
              {recommendedGames.slice(0, 4).map((item) => (
                <img
                  className="gameImg"
                  src={require(`~static/recommended/${item.id}.jpg`)}
                ></img>
              ))}
            </div>
            <div className="bottom">
              <img
                className="gameImg bigImg"
                src={require(`~static/recommended/${recommendedGames[4].id}.jpg`)}
              ></img>
              <div className="right">
                {recommendedGames.slice(5).map((item) => (
                  <img
                    className="gameImg"
                    src={require(`~static/recommended/${item.id}.jpg`)}
                  ></img>
                ))}
              </div>
            </div>
          </div>
        </div>
        <div className="block-categories">
          <div className="title">Hot Games</div>
          <div className="block-categories-games">
            {otherGames.map((item) => (
              <img
                className="gameImg"
                src={require(`~static/other/${item.id}.jpg`)}
              ></img>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Main;
