import "./index.less";
import { useRef, useState, useEffect } from "react";
import { Swiper } from "antd-mobile";

import { SwiperRef } from "antd-mobile/es/components/swiper";
const Main = () => {
  const [bannerItem, setBannerItem] = useState([]);
  const init = () => {
    const bannerItems = require.context("~static/banner", true, /\.jpg$/);
    const bannerArr = bannerItems.keys().map((key, index) => {
      const item = bannerItems[key];
      console.log(item.id);
      return (
        <Swiper.Item key={item.id}>
          <div>
            {/* <img src={`~static/banner/${item[key].id}.jpg`}></img> */}
          </div>
        </Swiper.Item>
      );
    });
    setBannerItem(bannerArr);
  };

  useEffect(() => {
    init();
  }, []);

  return (
    <div className="gameListBox">
      {/* 轮播图 */}
      <Swiper autoplay>{bannerItem}</Swiper>
    </div>
  );
};

export default Main;
