import "./index.less";
import { useRef, useState, useEffect } from "react";
import { Swiper } from "antd-mobile";

import { bannerItems } from "../../json/game/ index";

import { SwiperRef } from "antd-mobile/es/components/swiper";
const Main = () => {
  const [banner, setBanner] = useState([]);
  const init = () => {
    // const bannerItems = require.context("~static/banner", true, /\.jpg$/);
    const bannerArr = bannerItems.map((item, index) => {
      return (
        <Swiper.Item key={item.id}>
          <div>
            <img
              className="swiperImage"
              src={require(`~static/banner/${item.id}.jpg`)}
            ></img>
          </div>
        </Swiper.Item>
      );
    });
    setBanner(bannerArr);
  };

  useEffect(() => {
    init();
  }, []);

  return (
    <div className="gameListBox">
      {/* 轮播图 */}
      <Swiper
        loop
        autoplay
        allowTouchMove
        autoplayInterval="1000"
        indicator={(total, current) => (
          <div className="customIndicator">
            {bannerItems.map((item, index) => (
              <div
                className={
                  current === index
                    ? "customIndicatorItem currentItem"
                    : "customIndicatorItem"
                }
              >
                <img src={require(`~static/banner/${item.id}-small.jpg`)}></img>
              </div>
            ))}
          </div>
        )}
      >
        {banner}
      </Swiper>
    </div>
  );
};

export default Main;
